import React, {useState} from 'react'


export default function TextForm(props) {
    const[Text,setText]=useState('Enter Text Here'); 
    const HandleUpCase= () => {
        console.log("Upper case Was Clicked"+Text)
        setText(Text.toUpperCase());

    }
    const HandleLowerCase= () => {
        console.log("Lower Case Was Clicked"+Text)
        setText(Text.toLowerCase());

    }
    const HandleOnChange= (event) =>{
        console.log("Handel On Change") 
        setText(event.target.value)

    }
  return (
    <div>
        <h1>{props.heading}</h1>

   <div className="mb-3">
   <textarea className="form-control" id="exampleFormControlTextarea1" value={Text} onChange={HandleOnChange} rows="8"></textarea>
   </div>
   <button type="submit" className="btn btn-primary mb-3" onClick={HandleUpCase}>ConvertToUpperCase</button>
   <></>
   <button type="submit" className="btn btn-primary mb-3" onClick={HandleLowerCase}>ConvertToLowerCase</button>


    </div>
  )
}
